// Render Sidebar
const sidebar = document.querySelector(".sidebar");
sidebar.innerHTML = Sidebar(sidebarData, sidebarIcon);
function Sidebar(sidebarData, sidebarIcon) {
  return `
  <div class="sidebar__head">
    <div class = 'sidebar__logo'>
    </div>
  </div>
  <div class="sidebar__body">
    ${sidebarData.main
      .map(
        (obj) => `
        <div class = 'list' title = '${obj.title}'>
          <div class = 'list__icon'>${obj.icon}</div>
          <div class = 'list__title' other = '${obj.other}'> ${obj.title} </div>
        </div>
      `
      )
      .join("")}
  </div>
  <div class = 'sidebar__divide'></div>
  <div class="sidebar__footer">
    ${sidebarData.scroll
      .map(
        (obj) => `
        <div class = 'list' title = '${obj.title}'>
          <div class = 'list__icon'>${obj.icon}</div>
          <div class = 'list__title' other = '${obj.other}'>${obj.title}</div>
        </div>
      `
      )
      .join("")}
    ${sidebarData.banner
      .map(
        (obj) => `
        <div class = 'banner'>
          <p>${obj.text}</p>
          <button>${obj.title}</button>
        </div>
      `
      )
      .join("")}
    
  </div>
  <button class = 'sidebar__add-playlist'>
    <span class = 'icon'> ${sidebarIcon.iconAdd} </span>
    <span>Tạo playlist mới</span>
  </button>

  <button class = 'btn-expanded'>
    ${sidebarIcon.iconRight}
  </button>

  `;
}

const listsTitle = document.querySelectorAll(".list__title");

for (const list of listsTitle) {
  const other = list.getAttribute("other");
  if (other === "radio") {
    let figure = document.createElement("figure");
    figure.innerHTML = sidebarIcon.iconNew;
    list.appendChild(figure);
  }
  if (other === "radio" || other === "zingchart" || other === "nhac-moi") {
    let iconPlay = document.createElement("div");
    iconPlay.classList.add("list__icon-play");
    iconPlay.innerHTML = sidebarIcon.iconPlay;
    list.appendChild(iconPlay);
  }

}

const btnExpanded = document.querySelector(".btn-expanded");
btnExpanded.addEventListener("click", function () {
  sidebar.classList.toggle("is-expanded");
  btnExpanded.innerHTML === sidebarIcon.iconLeft
    ? (btnExpanded.innerHTML = sidebarIcon.iconRight)
    : (btnExpanded.innerHTML = sidebarIcon.iconLeft);
});

const lists = document.querySelectorAll('.list')
const contents = document.querySelectorAll('.content')

lists[0].classList.add('is-active')
lists.forEach((list, index) => {

  list.onclick = () => {
    contents.forEach(elem => elem.classList.remove('active'))
    contents[index].classList.add('active')
    lists.forEach(list => list.classList.remove('is-active'))
    list.classList.add('is-active')
  }
})